import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import {NavigationContainer} from '@react-navigation/native';
import {CreateBottomTabNavigator} from 'react'
import {WebView} from 'react-native-webview';


const Tab = CreateBottomTabNavigator();



export default function App() {
  return (
    <NavigationContainer>
      <Tab.Navigator>
        <Tab.Screen name="Aba PDF" component={Tela01}></Tab.Screen>
        <Tab.Screen name="Aba Pag Vídeo" component={Tela02}></Tab.Screen>
        <Tab.Screen name="Aba Vídeo" component={Tela03}></Tab.Screen>
      </Tab.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

function Tela01(){
  return(
  <WebView source={{uri:'https://prefeiturasrc.mg.gov.br/docs/Documentos/Decretos/2021/Decreto_468-2021.pdf'}}></WebView>
  );
}

function Tela02(){
  return(
  <WebView style={{flex:1}} javaScriptEnabled={true} source={{uri:'https://youtu.be/lID9kEd6Pdc'}}></WebView>
  );
}

function Tela03(){
  return(
    <WebView style={{flex:1}} javaScriptEnabled={true} source={{html:"<html><body><iframe width='800' height='400' src='https://youtu.be/aQXEftUhtM8?t=192' frameboder='0' allowfullscreen></iframe></body></html>"}}></WebView>
  );
}
